import { Component, OnInit } from '@angular/core';
import { CitaService } from '../servicios/cita.service';
import { map } from 'rxjs/operators';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-citas',
  templateUrl: './citas.page.html',
  styleUrls: ['./citas.page.scss'],
})
export class CitasPage implements OnInit {
  citas?:  any[];
  handlerMessage = '';
  roleMessage = '';

  constructor(
    private citaService: CitaService,
    private alertController: AlertController
  ) { }

  ngOnInit() {
    this.listAllCitas();  
  }

  listAllCitas(){
    this.citaService.getAll().snapshotChanges().pipe(
      map(changes =>
        changes.map(c =>
          ({ id: c.payload.doc.id, ...c.payload.doc.data() })
        )
      )
    ).subscribe(data => {
      this.citas = data;
      console.log(this.citas);
    });
  }

  async deleteCita(id: string){    
    const alert = await this.alertController.create({
      header: '¿Esta seguro que desea eliminar el registro?',
      buttons: [
        {
          text: 'No',
          role: 'cancel',          
        },
        {
          text: 'Si',
          role: 'confirm',
          handler: () => {
            this.citaService.delete(id).then(() => {
              this.listAllCitas();
              console.log('Cita eliminada exitosamente!')
            }).catch(err => console.log(err));
          },
        },
      ],
    });

    await alert.present();    
  }


}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Tus citas</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-list class="ios list-ios hydrated">
    <ion-list-header class="ios hydrated">
      Citas agendadas
    </ion-list-header>
    <ion-item *ngFor="let cita of citas" class="user-list">
      <ion-label>
        <h5>
          <ion-icon name="person-circle-outline"></ion-icon> &nbsp; {{cita.nombre}}
        </h5>
        <p>
          <ion-icon name="mail-outline"></ion-icon> {{cita.correo}}
        </p>
        <p>
          <ion-icon name="call"></ion-icon> {{cita.celular}}
        </p>
      </ion-label>
      <div item-end>
        <button ion-button clear [routerLink]="['/editar-cita/', cita.id]">
          <ion-icon name="create" style="zoom:2.0; background-color: white;"></ion-icon>
        </button>
        <button ion-button clear (click)="deleteCita(cita.id)">
          <ion-icon name="trash" style="zoom:2.0; background-color: white"></ion-icon>
        </button>
      </div>
    </ion-item>
  </ion-list>  

</ion-content>
