import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormBuilder } from "@angular/forms";
import { CitaService } from '../servicios/cita.service';
import { Cita } from '../entidades/Cita';

@Component({
  selector: 'app-crear-cita',
  templateUrl: './crear-cita.page.html',
  styleUrls: ['./crear-cita.page.scss'],
})
export class CrearCitaPage implements OnInit {

  citaForm!: FormGroup;
  idDoctor = localStorage.getItem("idDoctor");
  datos: Cita = { 
    paciente:'',
    correo: '',
    celular: '',
    idDocotor: 'this.idDoctor'
  }

  constructor(
    private citaService: CitaService,
    private router: Router,
    public fb: FormBuilder
  ) { }

  ngOnInit() {
    this.citaForm = this.fb.group({
      paciente: [''],
      correo: [''],
      celular: ['']
    })
  }

  saveCita() {       
    this.citaService.create(this.datos);
    console.log('Cita creada exitosamente!');
    this.citaForm.reset();
    this.router.navigate(['/citas']);
    return true;
  }

}
