import { Component, OnInit } from '@angular/core';
import { AuthService } from '../servicios/auth.service';
import { ToastController } from '@ionic/angular';
import { map } from 'rxjs/operators';
import { DoctorService } from '../servicios/doctor.service';

@Component({
  selector: 'app-doctores',
  templateUrl: './doctores.page.html',
  styleUrls: ['./doctores.page.scss'],
})
export class DoctoresPage implements OnInit {

  login : boolean = false;
  doctores?:any[];

  constructor(
    private auth :AuthService,
    private toastController: ToastController,
    private doctorService: DoctorService,
  ) { 
    this.auth.estadoLogin().subscribe(res =>{
      if(res){
        this.mostrarMensaje("Esta logueado!!")
        this.login= true
      }else{
        this.mostrarMensaje("No esta logueado!!")
        this.login = false
      }
    })
  }

  ngOnInit() {
    this.listAllDoctores();
  }

  listAllDoctores(){
    this.doctorService.getAll().snapshotChanges().pipe(
      map(changes =>
        changes.map(c =>
          ({ id: c.payload.doc.id, ...c.payload.doc.data() })
        )
      )
    ).subscribe(data => {
      this.doctores = data;
      console.log(this.doctores);
      data.forEach(function(numero) {
        localStorage.setItem("idDoctor", numero.id)
      });
    });
  }

  async mostrarMensaje(mensaje: any){
    const toast = await this.toastController.create({
      position: 'top',
      message: mensaje,
      duration:3000
    });
    toast.present();
  }

}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
      <ion-title>Listado doctores</ion-title>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>

  <ion-item *ngFor="let doctor of doctores" class="user-list">
    <ion-label>
      <h5>
        <ion-icon name="person"></ion-icon> &nbsp; {{doctor.nombre}} 
      </h5>
      <p>
        <ion-icon name="person"></ion-icon> &nbsp; {{doctor.apellido}}
      </p>
      <p>
        <ion-icon name="home-outline"></ion-icon> &nbsp; {{doctor.direccion}}
      </p>
      <p>
        <ion-icon name="mail-outline"></ion-icon> &nbsp; {{doctor.email}}
      </p>
      <p>
        <ion-icon name="business-outline"></ion-icon> &nbsp; {{doctor.empresa}}
      </p>
      <p>
        <ion-icon name="phone-portrait-outline"></ion-icon> &nbsp; {{doctor.telefono}}
      </p>
    </ion-label>

    <div item-end>
      <button ion-button clear [routerLink]="['/crear-cita']">
        <ion-icon name="add-circle-outline" style="zoom:2.0; background-color: white;"></ion-icon> 
      </button>
    </div>

  </ion-item>

</ion-content>
