import { Component, OnInit } from '@angular/core';
import { PacienteService } from '../servicios/paciente.service';
import { map } from 'rxjs/operators';
import { AlertController } from '@ionic/angular';

@Component({
  selector: 'app-mi-info',
  templateUrl: './mi-info.page.html',
  styleUrls: ['./mi-info.page.scss'],
})
export class MiInfoPage implements OnInit {
  pacientes?:any[];

  constructor(
    private pacienteService: PacienteService,
    private alertController: AlertController
  ) { }

  ngOnInit() {
    this.listAllPacientes();
  }

  listAllPacientes(){
    this.pacienteService.getAll().snapshotChanges().pipe(
      map(changes =>
        changes.map(c =>
          ({ id: c.payload.doc.id, ...c.payload.doc.data() })
        )
      )
    ).subscribe(data => {
      this.pacientes = data;
      console.log(this.pacientes);
    });
  }

  async deleteCita(id: string){    
    const alert = await this.alertController.create({
      header: '¿Esta seguro que desea eliminar el registro?',
      buttons: [
        {
          text: 'No',
          role: 'cancel',          
        },
        {
          text: 'Si',
          role: 'confirm',
          handler: () => {
            this.pacienteService.delete(id).then(() => {
              this.listAllCitas();
              console.log('Cita eliminada exitosamente!')
            }).catch(err => console.log(err));
          },
        },
      ],
    });
    await alert.present();    
  }

}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Listado de mis mascotas</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>

  <ion-item *ngFor="let paciente of pacientes" class="user-list">
    <ion-label>
      <h5>
        <ion-icon name="person"></ion-icon> {{paciente.nombre}}
      </h5>
      <p>
        <ion-icon name="call"></ion-icon> {{paciente.numCel}}
      </p>
      <p>
        <ion-icon name="paw"></ion-icon> {{paciente.nombreMascota}}
      </p>
      <p>
        <ion-icon name="paw"></ion-icon> {{paciente.edadMascota}}
      </p>
      <p>
        <ion-icon name="paw"></ion-icon> {{paciente.tipoMascota}}
      </p>
      <p>
        <ion-icon name="paw"></ion-icon> {{paciente.razaMascota}}
      </p>
    </ion-label>
  </ion-item>

  <ion-button type="submit" expand="block" color="dark" routerLink="../crear-pacientes">Crear Paciente</ion-button>
</ion-content>
